esKjuLazyLoading=function(a){this.init(a)};$.extend(esKjuLazyLoading.prototype,{init:function(a){this.container=$(a.container);this.offset=this.container.offset();this.loading=false;this.url=this.container.attr("data-request");this.options=$.extend({tolerance:200,classLoading:"esKju-lazyloading",classLoaded:"esKju-lazyloading-loaded",pageAlias:"page",page:1,maxPages:false,lazyImagePlugin:{enable:true,classLoading:"esKju-lazyimage"}},a);this.bindScroll()},loadNextPage:function(){var a=this;$.get(this.url,{ajax:1,page:a.options.page},function(b){a.container.append(b);a.container.addClass(a.options.classLoaded);if(a.options.lazyImagePlugin.enable){if(typeof(LazyImage)==="undefined"){console.debug("LazyImage plugin is not loaded")}else{new LazyImage(a.container.find("."+a.options.lazyImagePlugin.classLoading))}}if(a.container.find("."+a.options.classLoading).length>0){if(!a.options.maxPages||a.options.maxPages>a.options.page){new esKjuLazyLoading({container:a.container.find("."+a.options.classLoading),page:a.options.page+1,maxPages:a.options.maxPages})}}})},bindScroll:function(){var a=this;a.update();$(window).scroll(function(){a.update()});$(window).resize(function(){a.update()})},update:function(){var a=this;this.offset=this.container.offset();if(!a.loading&&$(window).scrollTop()+$(window).height()-a.options.tolerance>a.offset.top){a.loading=true;a.loadNextPage()}}});
